#true = condição verdadeira
#false = condição falsa

#condicionais
if True: #se for verdadeiro, execute.
    print("bloco if vai ser executado")
else: #se não...
    print("bloco else não será executado")

#operadores lógicos: and e or
#and
if True and True:
    print("Bloco será executado.")

if True and False:
    print("Bloco não será executado")

if False and False:
    print("Bloco não será executado.")

#or
if True or True:
    print("Bloco será executado.")

if True or False:
    print("Bloco será executado.")

if False or False:
    print("Bloco não será executado.")