#Inteiro
numero_inteiro = 3
print("Inteiro =", numero_inteiro) 

#Real com ponto flutuante
numero_real = 3.14
print("Real =", numero_real)

#função type()
print("Tipo da variável inteiro =", type(numero_inteiro))
print("Tipo da variável real =", type(numero_real))

"""operações aritméticas"""
#soma +
soma = 1 + 1
print("1 + 1 =", soma)

#subtração - 
subtracao = 1 - 1
print("1 - 1 =", subtracao)

#multiplicacao *
multiplicacao = 2 * 2
print("2 x 2 =", multiplicacao)

#divisao /
divisao = 10 / 2
print("10 / 2 =", divisao)
print("tipo do resultado da div: ", type(divisao))
#int
print("Valor em inteiro:", int(divisao)) #função que converte para inteiro

#função float
print("Valor da soma em real:", float(soma))

#módulo %
modulo = 5 % 2 #resto da divisão
print("Resto de 5 / 2 =", modulo)